let form=document.querySelector(".form-container"),buttonForm=document.querySelector(".button-open-form"),searchForm=document.querySelector(".search-form"),inputDate=document.querySelector(".input-date"),inputNumber=document.querySelector(".input-number");buttonForm.addEventListener("click",function(e){e.preventDefault(),form.classList.toggle("modal-hidden"),form.classList.toggle("modal-open")}),searchForm.addEventListener("submit",function(e){inputDate.value&&inputNumber.value||(e.preventDefault(),console.log("Заполните все поля"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(form.classList.toggle("modal-hidden"),form.classList.toggle("modal-open"))});